import React from "react";
import styled, { css } from "styled-components";
import backgroundImg from "../img/review.png";
import { MdRateReview } from "react-icons/md";

const InnerOpacityBlockStyle = css`
  align-items: none;
`;

const ReviewContainer = styled.div`
  width: 100%;
  height: 400px;
  background: #f8f9fa;
  background: url(${(props) => props.img});
`;

const OpacityBlock = styled.div`
  display: flex;
  width: 100%;
  height: 400px;
  background-color: rgba(0, 0, 0, 0.3);
  z-index: 5;
  display: flex;
  justify-content: center;
  align-items: center;
  ${InnerOpacityBlockStyle};
`;

const ReviewBoxes = styled.div`
  display: flex;
  justify-content: center;
  width: 350px;
  height: 250px;
  border: 2px solid #ffd43b;
  border-radius: 6px;
  & + & {
    margin-left: 1rem;
  }
`;

const ReviewBoxesContents = styled.div`
  width: 80%;
  p {
    color: white;
  }
`;

const ReviewHeader = styled.div`
  width: 100%;
  color: white;
  border-bottom: 2px solid white;
  font-size: 1.375rem;
`;

const Review = () => {
  return (
    <>
      <ReviewContainer img={backgroundImg}>
        <OpacityBlock>
          <ReviewBoxes>
            <OpacityBlock style={{ height: "250px" }}>
              <ReviewBoxesContents>
                <ReviewHeader>Admin 님</ReviewHeader>
                <p>아 너무 재미있었어요</p>
              </ReviewBoxesContents>
            </OpacityBlock>
          </ReviewBoxes>
        </OpacityBlock>
      </ReviewContainer>
    </>
  );
};

export default Review;
