import React from "react";
import styled, { css } from "styled-components";
import MapAPI from "./MapAPI";

const FotterContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: 500px;
  background: #495057;
`;

const ContactContainer = styled.div`
  margin-left: 10rem;
  width: 500px;

  h1 {
    color: white;
    margin-bottom: 0.5rem;
  }
`;

const ContactForm = styled.form`
  display: grid;
  grid-template-columns: 180px 180px;
  grid-auto-rows: 60px 60px 130px 60px;
  justify-content: center;
  align-items: center;
  width: 380px;
  height: 268px;
  background: white;
  padding: 1rem;
  border-radius: 6px;

  .span {
    grid-column: 1/3;
  }
`;

const sizes = {
  small: {
    width: "150px",
    height: "40px",
  },
  medium: {
    width: "330px",
    height: "30px",
  },
  large: {
    width: "330px",
    height: "120px",
  },
};

const SizeStyles = css`
  ${({ size }) => css`
    width: ${sizes[size].width};
    height: ${sizes[size].height};
  `}
`;

const CustomInput = styled.input`
  outline: none;
  border: 2px solid #63e6be;
  /* border-radius: 4px; */
  font-size: 1rem;
  padding-left: 1rem;
  ${SizeStyles};
  ${(props) =>
    props.row === 2
      ? css`
          grid-column-start: 1;
          grid-column-end: 2;
        `
      : css``}
`;

const CustomButton = styled.button`
  width: 350px;
  height: 40px;
  outline: none;
  border: none;
  /* border-radius: 4px; */
  background: #63e6be;
  font-size: 1.25rem;
  padding-left: 1rem;
  grid-row-start: 4;
  color: white;
`;

const Fotter = () => {
  return (
    <FotterContainer>
      <MapAPI />
      <ContactContainer>
        <h1>Contact</h1>
        <ContactForm>
          <CustomInput type="text" placeholder="Name" size="small" />
          <CustomInput type="text" placeholder="Email" size="small" />
          <CustomInput
            className="span"
            type="text"
            placeholder="Subject"
            size="medium"
          />

          <CustomInput
            className="span"
            type="text"
            placeholder="..."
            size="large"
            rowSize={2}
          />
          <CustomButton>Click</CustomButton>
        </ContactForm>
      </ContactContainer>
    </FotterContainer>
  );
};

export default Fotter;
