import React, { useState, useEffect, useRef } from "react";
import styled from "styled-components";
import test1 from "../img/EverySports.png";
import test2 from "../img/EverySports_1.png";
import test3 from "../img/EverySports.png";
import Slide from "./Slide";

const Container = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  border: 1px solid red;
  flex-direction: column;
`;

const SliderOutterContainer = styled.div`
  width: 500px;
  height: 400px;
  overflow: hidden;
  border: 1px solid red;
`;

const SliderInnerContainer = styled.div`
  display: flex;
  border: 1px solid gold;
`;

const CurrentButton = styled.button`
  width: 1.25rem;
  height: 0.85rem;
  border-radius: 45%;
  background: #495057;
  border: none;
  padding: 0;
  cursor: pointer;
  outline: none;
  margin-left: 0.125rem;
`;

const AnotherButton = styled.button`
  cursor: pointer;
  width: 1.25rem;
  height: 0.85rem;
  border-radius: 45%;
  background: #ced4da;
  border: none;
  padding: 0;
  transition: 0.25s ease-in;
  outline: none;
  margin-left: 0.125rem;
  &:hover {
    background: #495057;
  }
`;
const AAA = styled.div`
  display: flex;
  justify-content: center;
`;

const SlideButtonGroup = styled.div`
  display: flex;
  justify-content: center;
`;

const TOTAL_SLIDES = 2;
const initialSlideButton = Array.apply(null, Array(TOTAL_SLIDES + 1)).map(
  Number.prototype.valueOf,
  0
);
initialSlideButton[0] = 1;

const Slider = () => {
  const [slideButton, setSlideButton] = useState(initialSlideButton);
  const slideRef = useRef(null);

  useEffect(() => {
    slideRef.current.style.transition = "all 0.5s ease-in-out";
    slideRef.current.style.transform = `translateX(-${slideButton.indexOf(
      1
    )}00%)`;
  }, [slideButton]);

  const onButtonSlide = (e) => {
    const nextSlideIndex = e.target.value;
    const nextSlideState = Array.apply(null, Array(TOTAL_SLIDES + 1)).map(
      Number.prototype.valueOf,
      0
    );
    nextSlideState[nextSlideIndex] = 1;
    setSlideButton(nextSlideState);
  };

  return (
    <Container>
      <AAA>
        <SliderOutterContainer>
          <SliderInnerContainer ref={slideRef}>
            <Slide img={test1} />
            <Slide img={test2} />
            <Slide img={test3} />
          </SliderInnerContainer>
        </SliderOutterContainer>

        <SlideButtonGroup>
          {slideButton.map((value, index) =>
            value === 1 ? (
              <CurrentButton
                key={index}
                onClick={onButtonSlide}
                value={index}
              />
            ) : (
              <AnotherButton
                key={index}
                onClick={onButtonSlide}
                value={index}
              />
            )
          )}
        </SlideButtonGroup>
      </AAA>
    </Container>
  );
};

export default Slider;
