import React from "react";
import styled, { css } from "styled-components";
import backgroundImg from "../img/review.png";
import { MdRateReview } from "react-icons/md";

const InnerReviewBlockStyle = css`
  background-color: rgba(0, 0, 0, 0.8);
  height: 350px;
`;

const ReviewContainer = styled.div`
  width: 100%;
  height: 400px;
  background: #f8f9fa;
  background: url(${(props) => props.img});
`;

const OpacityBlock = styled.div`
  width: 100%;
  height: 400px;
  background-color: rgba(0, 0, 0, 0.4);
  z-index: 5;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;

  ${(props) =>
    props.isInner &&
    css`
      /* background-color: rgba(0, 0, 0, 0.8); */
      h1 {
        color: white;
      }
    `}
`;

const ReviewGroup = styled.div`
  display: flex;
  justify-content: center;
  width: 350px;
  height: 350px;
  border: 2px solid #ffd43b;
  border-radius: 6px;
`;

const Review = () => {
  return (
    <>
      <ReviewContainer img={backgroundImg}>
        <OpacityBlock isInner={false}>
          <ReviewGroup>
            <OpacityBlock style={InnerReviewBlockStyle}>
              <h1> test</h1>
            </OpacityBlock>
          </ReviewGroup>
        </OpacityBlock>
      </ReviewContainer>
    </>
  );
};

export default Review;
