import React from "react";
import styled, { css } from "styled-components";
import MapAPI from "./MapAPI";

const FotterContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: 500px;
  background: #495057;
`;

const ContactContainer = styled.div`
  margin-left: 10rem;
  width: 500px;

  h1 {
    color: white;
    margin-bottom: 0.5rem;
  }
`;

const ContactForm = styled.form`
  display: grid;
  grid-template-columns: 160px 160px;
  grid-auto-rows: 40px 40px 40px;
  justify-content: center;
  width: 400px;
  height: 268px;
  background: white;
  padding: 1rem;
  border-radius: 6px;
`;

const InputPropsControll = css`
  grid-column-start: 1;
  grid-column-end: 2;
`;

const CustomInput = styled.input`
  /* width: ${(props) => (props.isSmall ? "300px" : "150px")}; */
  width : 150px;
  ${(props) =>
    props.isLarge === true &&
    css`
      width: 300px;
      grid-column-start: 1;
      grid-column-end: 2;
    `}

  height: 30px;
  outline: none;
  border: 2px solid #63e6be;
  border-radius: 4px;
  font-size: 1.25rem;
  padding-left: 1rem;
`;

const Fotter = () => {
  return (
    <FotterContainer>
      <MapAPI />
      <ContactContainer>
        <h1>Contact</h1>
        <ContactForm>
          <CustomInput type="text" placeholder="Name" isLarge={false} />
          <CustomInput type="text" placeholder="Email" isLarge={false} />
          <CustomInput type="text" placeholder="LLL" isLarge={true} />
          <CustomInput type="text" placeholder="LLL" isLarge={true} />
        </ContactForm>
      </ContactContainer>
    </FotterContainer>
  );
};

export default Fotter;
