import React from "react";
import styled, { css } from "styled-components";
import MapAPI from "./MapAPI";
import { lighten, darken } from "polished";

const FotterContainer = styled.div`
  justify-content: center;
  align-items: center;
  width: 100%;
  height: auto;
  background: #495057;
`;

const APIsContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  transform: translate(3%, 0);
  @media screen and (max-width: 991px) {
    flex-direction: column;
  }
`;

const ContactContainer = styled.div`
  margin-left: 3rem;
  width: 500px;
  h1 {
    color: white;
    margin-bottom: 0.5rem;
  }
  @media screen and (max-width: 991px) {
    margin-left: 0rem;
  }
`;

const ContactForm = styled.form`
  @media screen and (min-width: 768px) {
    display: grid;
    grid-template-columns: 50% 50%;
    grid-template-rows: 40px 40px 130px 60px;
    justify-content: center;
    align-items: center;
    width: 468px;
    height: 268px;
    background: white;
    padding: 1rem;
    border-radius: 6px;
    margin-right: 0;
    .span {
      grid-column: 1/3;
    }
  }

  @media screen and (max-width: 767px) {
    display: flex;
    flex-direction: column;
    width: 290px;
    height: 200px;
  }
`;

const sizes = {
  small: {
    width: "80%",
    height: "30px",
  },
  medium: {
    width: "90%",
    height: "30px",
  },
  large: {
    width: "90%",
    height: "120px",
  },
};

const SizeStyles = css`
  ${({ size }) => css`
    width: ${sizes[size].width};
    height: ${sizes[size].height};
  `}
`;

const CustomInput = styled.input`
  outline: none;
  border: 2px solid #63e6be;
  /* border-radius: 4px; */
  font-size: 1rem;
  padding-left: 1rem;
  ${SizeStyles};
  ${(props) =>
    props.row === 2
      ? css`
          grid-column-start: 1;
          grid-column-end: 2;
        `
      : css``}
`;

const CustomButton = styled.button`
  width: 350px;
  height: 40px;
  outline: none;
  border: none;
  /* border-radius: 4px; */
  background: #63e6be;
  font-size: 1.25rem;
  padding-left: 1rem;
  grid-row-start: 4;
  color: white;
  &:hover {
    background: ${lighten(0.1, "#63e6be")};
  }
  &:active {
    background: ${darken(0.1, "#63e6be")};
  }
`;

const FotterScript = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  color: white;
`;

const Fotter = () => {
  return (
    <FotterContainer>
      <APIsContainer>
        <MapAPI />
        <ContactContainer>
          <h1>Contact</h1>
          <ContactForm>
            <CustomInput type="text" placeholder="Name" size="small" />
            <CustomInput type="text" placeholder="Email" size="small" />
            <CustomInput
              className="span"
              type="text"
              placeholder="Subject"
              size="medium"
            />
            <CustomInput
              className="span"
              type="text"
              placeholder="Type your message here..."
              size="large"
            />
            <CustomButton>Send</CustomButton>
          </ContactForm>
        </ContactContainer>
      </APIsContainer>
      <FotterScript>
        {/* <p>
          500 Terry Francois Street San Francisco, CA 94158 Email: es@es.com
          Tel: 123-456-7890
        </p>
        <br />
        <p>Â© 2020 by every sports.</p> */}
      </FotterScript>
    </FotterContainer>
  );
};

export default Fotter;
