import React from "react";
import styled, { css } from "styled-components";
import backgroundImg from "../img/review.png";
import { MdRateReview } from "react-icons/md";

const ReviewContainer = styled.div`
  width: 100%;
  height: 400px;
  background: #f8f9fa;
  background: url(${(props) => props.img});
`;

const OpacityBlock = styled.div`
  width: 100%;
  height: 400px;
  background-color: rgba(0, 0, 0, 0.4);
  z-index: 5;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;

  ${(props) =>
    props.isInner &&
    css`
      background-color: rgba(0, 0, 0, 0.8);
      h1 {
        color: white;
      }
    `}
`;

const ReviewBoxes = styled.div`
  display: flex;
  justify-content: center;
  width: 350px;
  height: 350px;
  border: 2px solid #ffd43b;
  border-radius: 6px;
`;

const ReviewHeader = styled.h1`
  color: white;
  border-bottom: 1px solid white;
`;

const Review = () => {
  return (
    <>
      <ReviewContainer img={backgroundImg}>
        <OpacityBlock isInner={false}>
          <ReviewBoxes>
            <OpacityBlock style={{ height: "350px" }}>
              <ReviewHeader>test</ReviewHeader>
            </OpacityBlock>
          </ReviewBoxes>
        </OpacityBlock>
      </ReviewContainer>
    </>
  );
};

export default Review;
