import React from "react";
import styled, { css } from "styled-components";
import MapAPI from "./MapAPI";
import { lighten, darken } from "polished";

const FotterContainer = styled.div`
  justify-content: center;
  align-items: center;
  width: 100%;
  height: auto;
  background: #495057;
`;

const APIsContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  /* transform: translate(, 0); */
  @media screen and (max-width: 991px) {
    flex-direction: column;
  }
`;

const ContactContainer = styled.div`
  margin-left: 3rem;
  width: 500px;
  h1 {
    color: white;
    margin-bottom: 0.5rem;
  }
  @media screen and (max-width: 991px) {
    margin-left: 0rem;
  }
`;

const ContactForm = styled.form`
  display: flex;
  flex-direction: column;
  width: 500px;
  height: 200px;
  padding: 1rem;
  background: white;
  border-radius: 6px;
`;

const inputStyles = {
  small: {
    height: "40px",
  },
  large: {
    height: "120px",
  },
};

const CustomInput = styled.input`
  outline: none;
  border: 2px solid #63e6be;
  border-radius: 4px;
  padding: 0.125rem 1rem;
  font-size: 1rem;
  height: ${(props) => (props.isSmall ? "30px" : "120px")};
  & + & {
    margin-top: 0.5rem;
  }
`;

const CustomButton = styled.button`
  width: 100%;
  grid-column: 1/3;
  height: 40px;
  outline: none;
  border: none;
  /* border-radius: 4px; */
  background: #63e6be;
  font-size: 1.25rem;
  padding-left: 1rem;
  grid-row-start: 4;
  color: white;
  &:hover {
    background: ${lighten(0.1, "#63e6be")};
  }
  &:active {
    background: ${darken(0.1, "#63e6be")};
  }
`;

const FotterScript = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  color: white;
`;

const Fotter = () => {
  return (
    <FotterContainer>
      <APIsContainer>
        <MapAPI />
        <ContactContainer>
          <h1>Contact</h1>
          <ContactForm>
            <CustomInput type="text" placeholder="Name" />
            <CustomInput type="text" placeholder="Email" />
            <CustomInput type="text" placeholder="Text" isSmall={false} />
            <CustomButton>SEND</CustomButton>
          </ContactForm>
        </ContactContainer>
      </APIsContainer>
      <FotterScript>
        {/* <p>
          500 Terry Francois Street San Francisco, CA 94158 Email: es@es.com
          Tel: 123-456-7890
        </p>
        <br />
        <p>Â© 2020 by every sports.</p> */}
      </FotterScript>
    </FotterContainer>
  );
};

export default Fotter;
