import React from "react";
import styled from "styled-components";
import MapAPI from "./MapAPI";

const FotterContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: 500px;
  background: #495057;
`;

const ContactContainer = styled.div`
  margin-left: 10rem;
  width: 500px;

  h1 {
    color: white;
    margin-bottom: 0.5rem;
  }
`;

const ContactForm = styled.form`
  display: grid;
  grid-template-columns: 200px 200px;
  grid-auto-rows: 40px 40px 40px;

  width: 400px;
  height: 268px;
  background: white;
  padding: 1rem;
  border-radius: 6px;
`;

const CustomInput = styled.input`
  width: ${(props) => (props.isSmall ? "150px" : "300px")};
  grid-column-start: ${(props) => props.gridRowLoc[0]};
  grid-column-end: ${(props) => props.gridRowLoc[1]};
  height: 30px;
  outline: none;
  border: 2px solid #63e6be;
  border-radius: 4px;
  font-size: 1.25rem;
  padding-left: 1rem;
`;

const Fotter = () => {
  return (
    <FotterContainer>
      <MapAPI />
      <ContactContainer>
        <h1>Contact</h1>
        <ContactForm>
          <CustomInput type="text" placeholder="Name" />
          <CustomInput type="text" placeholder="Email" />
          <CustomInput
            type="text"
            placeholder="Email"
            is
            isSmall={false}
            girdRowLoc={[1, 2]}
          />
          <CustomInput type="text" placeholder="Email" is isSmall={false} />
        </ContactForm>
      </ContactContainer>
    </FotterContainer>
  );
};

export default Fotter;
