import React from "react";
import styled from "styled-components";

const IMG = styled.img`
  width: 418px;
`;

const ImgDesc = styled.div`
  width: 300px;
  font-weight: bold;
  color: #868e96;
`;

const Slide = ({ img, value }) => {
  return (
    <>
      <IMG src={img} />
      {value === "AI" ? (
        <ImgDesc>인공지능을 이용하여 사용자의 골격을 디텍션 합니다.</ImgDesc>
      ) : value === "Front" ? (
        <ImgDesc>프론트</ImgDesc>
      ) : (
        <ImgDesc>백엔드</ImgDesc>
      )}
    </>
  );
};

export default Slide;
