import React from "react";
import styled from "styled-components";

const SlideGroup = styled.div`
  display: flex;
`;

const IMG = styled.img`
  width: 418px;
`;

const ImgDescContainer = styled.div`
  width: 300px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

const ImgDesc = styled.div`
  font-weight: bold;
  padding: 0px 1rem;
  color: #868e96;
`;

const Slide = ({ img, value }) => {
  return (
    <SlideGroup>
      <IMG src={img} />
      <ImgDescContainer>
        {value === "AI" ? (
          <>
            <h3>Deep Learning Engineer, CEO : Sungjin Kim</h3>
            <ImgDesc>
              인공지능을 이용하여 사용자의 골격을 디텍션 합니다.
            </ImgDesc>
          </>
        ) : value === "Front" ? (
          <ImgDesc>프론트</ImgDesc>
        ) : (
          <ImgDesc>백엔드</ImgDesc>
        )}
      </ImgDescContainer>
    </SlideGroup>
  );
};

export default Slide;
