import React from "react";
import styled from "styled-components";

const SlideGroup = styled.div`
  display: flex;
`;

const IMG = styled.img`
  width: 418px;
`;
//png size : 418 x 410

const ImgDescContainer = styled.div`
  width: 300px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

const ImgDesc = styled.div`
  font-weight: bold;
  padding: 0px 1rem;
  color: #868e96;
`;

const Slide = ({ img, value }) => {
  return (
    <SlideGroup>
      <IMG src={img} />
      <ImgDescContainer>
        {value === "AI" ? (
          <>
            <h3>인공지능 자세코칭 서비스</h3>
            <ImgDesc>
              인공지능 기술을 이용하여 사용자의 자세를 교정하며, 효율적인 운동
              습관에 대한 솔루션을 제공합니다.
            </ImgDesc>
          </>
        ) : value === "Front" ? (
          <>
            <h3>간단하고, 효율적인 운동 도우미</h3>
            <ImgDesc>
              자신의 운동 습관과 효율적인 운동을 돕기위한 다양한 컨텐츠들을
              만나보실 수 있습니다.
            </ImgDesc>
          </>
        ) : (
          <ImgDesc>백엔드</ImgDesc>
        )}
      </ImgDescContainer>
    </SlideGroup>
  );
};

export default Slide;
