import React from "react";
import styled, { css } from "styled-components";
import backgroundImg from "../img/review.png";
import { MdRateReview } from "react-icons/md";

const InnerOpacityBlockStyle = css`
  align-items: start;
  height: 186px;
  padding: 2rem;
`;

const ReviewContainer = styled.div`
  width: 100%;
  height: 400px;
  background: #f8f9fa;
  background: url(${(props) => props.img});
`;

const OpacityBlock = styled.div`
  display: flex;
  border-radius: 6px;
  flex-direction: column;
  width: 100%;
  height: 400px;
  background-color: rgba(0, 0, 0, 0.3);
  z-index: 5;
  display: flex;
  justify-content: center;
  align-items: center;
  ${(props) =>
    props.isOutter !== true &&
    css`
      ${InnerOpacityBlockStyle}
    `}
`;

const ReviewDesc = styled.div`
  width: 100%;
  display: absolute;
  left: 80%;
  padding-right: 2rem;

  color: white;
`;

const ReviewBoxGroup = styled.div``;

const ReviewBoxes = styled.div`
  display: flex;
  justify-content: center;
  width: 350px;
  height: 250px;
  border: 2px solid #ffd43b;
  border-radius: 6px;
  & + & {
    margin-left: 1rem;
  }
`;

const ReviewBoxesContents = styled.div`
  width: 100%;
  p {
    color: white;
  }
`;

const ReviewHeader = styled.div`
  width: 100%;
  color: white;
  border-bottom: 2px solid white;
  font-size: 1.375rem;
`;

const Review = () => {
  return (
    <>
      <ReviewContainer img={backgroundImg}>
        <OpacityBlock isOutter={true}>
          <ReviewDesc>
            <h1>주저 없는 선택을 위해</h1>
            <p>생생한 후기를 들려드립니다</p>
          </ReviewDesc>

          <ReviewBoxes>
            <OpacityBlock isOutter={false}>
              <ReviewBoxesContents>
                <ReviewHeader>Admin 님</ReviewHeader>
                <p>아 너무 재미있었어요</p>
              </ReviewBoxesContents>
            </OpacityBlock>
          </ReviewBoxes>
        </OpacityBlock>
      </ReviewContainer>
    </>
  );
};

export default Review;
